import React, { Component } from 'react';
import './../App.css';
import { TweenMax, Power2, TimelineLite } from 'gsap/TweenMax';

class Button extends Component {

    constructor(props) {
        super(props);
        this.myElement = null;
        this.myTween = null;
        this.runTestFunc = this.runTestFunc.bind(this);
    }

    runTestFunc() {
        this.props.sendFunction();
    }

    componentDidMount() {
        this.myTween = TweenMax.to(this.myElement, 2, {x: 50});
    }

    render() {
        return (
            <div>
            <div ref={div => this.myElement = div} >React-gsap TEST</div>
            <button onClick={this.runTestFunc}>
                <h3>Start</h3>
            </button>
            </div>
        );
    }

}

export default Button;
