import React, { Component } from 'react';
import { Container, Col } from 'reactstrap';
import './../App.css';

class Canvas extends Component {

    constructor(props) {
        super(props);
        this.state = {
            x: canvas.width / 2,
            y: canvas.height /2,
            dx: 2,
            dy: -2,
            ball: {radius: 10},
            paddle: {height: 10, width: 100},
            paddleX: (canvas.width - paddle.width) / 2,
            rightPressed: false,
            leftPressed: false,
            brickRowCount: 3,
            brickColumnCount: 6,
            brickWidth: 75,
            brickHeight: 20,
            brickPadding: 15,
            brickOffsetTop: 30,
            brickOffsetLeft: 30,
            brickX: (c * (brickWidth + brickPadding)) + brickOffsetLeft,
            brickY: (r * (brickHeight + brickPadding)) + brickOffsetTop
        }
    }

    componentDidMount() {
        this.updateCanvas();
    }

    updateCanvas() {
        const canvas = this.refs.canvas;
        const ctx = canvas.getContext("2d");
    }

    render() {
        bricks = [];
        for (var c = 0; c < brickColumnCount; c++) {
            bricks[c] = [];
            for (var r = 0; r < brickRowCount; r++) {
                bricks[c][r] = { x: 0, y: 0, status: 1 };
            }
        }
        return (
            <Container>
                <Col align="center">
                    <canvas id="game-canvas" height="600px" width="800px"></canvas>
                </Col>
            </Container>
        );
    }
}

export default Canvas;


