import React, { Component } from 'react';
import { Container, Col } from 'reactstrap';
import './../App.css';


class Canvas extends Component {

    constructor(props) {
        super(props);
        // this.state = {
        //     rightPressed: false,
        //     leftPressed: false
        // }
        this.canvasRef = React.createRef();
    }

    componentDidMount() {
        this.updateCanvas();
    }

    updateCanvas() {
        const canvas = this.canvasRef.current;
        const ctx = canvas.getContext('2d');
        this.x = canvas.width / 2;
        this.y = canvas.height / 2;
        var dx = 2;
        var dy = -2;

        var ball = {
            radius: 10
        };

        var paddle = {
            height: 10,
            width: 100,
        };

        var paddleX = (canvas.width - paddle.width) / 2;

        var rightPressed = false;
        var leftPressed = false;

        var brickRowCount = 3;
        var brickColumnCount = 6;
        var brickWidth = 75;
        var brickHeight = 20;
        var brickPadding = 15;
        var brickOffsetTop = 30;
        var brickOffsetLeft = 30;
        var brickX = (c * (brickWidth + brickPadding)) + brickOffsetLeft;
        var brickY = (r * (brickHeight + brickPadding)) + brickOffsetTop;

        var bricks = [];
        for (var c = 0; c < brickColumnCount; c++) {
            bricks[c] = [];
            for (var r = 0; r < brickRowCount; r++) {
                bricks[c][r] = { x: 0, y: 0, status: 1 };
            }
        }

        //keyHandler
        window.addEventListener("keydown", keyDownHandler, false);
        window.addEventListener("keyup", keyUpHandler, false);

        //collision detection between bricks and ball
        function collisionDetection() {
            for (var c = 0; c < brickColumnCount; c++) {
                for (var r = 0; r < brickRowCount; r++) {
                    var b = [c][r];
                    if (x < b.x && b.x + brickWidth && y > b.y && y + brickHeight) {
                        dy = -dy
                        b.status = 0;
                    }
                }
            }
        }

        function keyDownHandler(e) {
            if (e.keyCode === 39) {
                rightPressed = true;
            }
            else if (e.keyCode === 37) {
                leftPressed = true;
            }
        }
        function keyUpHandler(e) {
            if (e.keyCode === 39) {
                rightPressed = false;
            }
            else if (e.keyCode === 37) {
                leftPressed = false;
            }
        }

        function drawBall() {
            ctx.beginPath();
            ctx.arc(x, y, ball.radius, 0, Math.PI * 2);
            ctx.fillStyle = "#FF0049";
            ctx.fill();
            ctx.closePath();
        }

        function drawPaddle() {
            ctx.beginPath();
            ctx.rect(paddleX, canvas.height - paddle.height - 20, paddle.width, paddle.height);
            ctx.fillStyle = "#00CCDD";
            ctx.fill();
            ctx.closePath();
        }

        function drawBricks() {
            for (var c = 0; c < brickColumnCount; c++) {
                for (var r = 0; r < brickRowCount; r++) {
                    if (bricks[c][r].status === 1) {
                        var brickX = (c * (brickWidth + brickPadding)) + brickOffsetLeft;
                        var brickY = (r * (brickHeight + brickPadding)) + brickOffsetTop;
                        bricks[c][r].x = brickX;
                        bricks[c][r].y = brickY;
                        ctx.beginPath();
                        ctx.rect(brickX, brickY, brickWidth, brickHeight);
                        ctx.fillStyle = "#FFBB00";
                        ctx.fill();
                        ctx.closePath();
                    }
                }
            }
        }

        function draw() {
            ctx.clearRect(0, 0, canvas.width, canvas.height);
            drawBall();
            drawPaddle();
            collisionDetection();
            drawBricks();

            //ball reverse when hitting left & right
            if (x + dx > canvas.width - ball.radius || x + dx < ball.radius) {
                dx = -dx;
            }

            //ball reverse when hitting top
            if (y + dy < ball.radius) {
                dy = -dy;
            }

            if (x > paddleX && x < paddleX + paddle.width) {
                dy = -dy;
            }
            else {

            }

            if (rightPressed && paddleX < canvas.width - paddle.width) {
                paddleX += 7;
            }
            else if (leftPressed && paddleX > 0) {
                paddleX -= 7;
            }


            x += dx;
            y += dy;
        }

        requestAnimationFrame(draw);
    }

    render() {
        return (
            <Container>
                <Col align="center">
                    <canvas height="400px" width="600px" ref={this.canvasRef}></canvas>
                </Col>
            </Container>
        );
    }
}

export default Canvas;
